client/src/components/Header.tsx.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Download, UserPlus, Users, LogOut, Lock } from 'lucide-react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import ThemeToggle from './ThemeToggle';
import { useAuth } from '@/contexts/AuthContext';
import { useToast } from '@/hooks/use-toast';

interface HeaderProps {
  onNewParticipant?: () => void;
  onExport?: () => void;
  participantCount?: number;
}

export default function Header({ onNewParticipant, onExport, participantCount = 0 }: HeaderProps) {
  const { isAdmin, loginAdmin, logout } = useAuth();
  const { toast } = useToast();
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const [password, setPassword] = useState('');

  const handleAdminLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (loginAdmin(password)) {
      toast({
        title: 'Acesso Administrativo Ativado',
        description: 'Você agora tem acesso completo ao sistema.',
      });
      setShowAdminLogin(false);
      setPassword('');
    } else {
      toast({
        title: 'Senha Incorreta',
        description: 'A senha de administrador está incorreta.',
        variant: 'destructive',
      });
    }
  };

  const handleLogout = () => {
    logout();
    toast({
      title: 'Modo Público Ativado',
      description: 'Você está agora em modo de visualização.',
    });
  };

  return (
    <>
      <header className="sticky top-0 z-50 w-full border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
        <div className="container flex h-16 items-center justify-between px-4">
          <div className="flex items-center gap-3">
            <div className="flex items-center gap-2">
              <Users className="h-6 w-6 text-primary" />
              <div>
                <h1 
                  className={`font-display font-semibold text-lg leading-tight ${!isAdmin ? 'cursor-pointer hover-elevate' : ''}`}
                  onClick={() => !isAdmin && setShowAdminLogin(true)}
                  data-testid="text-title"
                >
                  14º Aniversário Filosofia X
                </h1>
                <p className="text-xs text-muted-foreground">
                  {participantCount} participante{participantCount !== 1 ? 's' : ''} {isAdmin && '• Administrador'}
                </p>
              </div>
            </div>
          </div>

          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="default"
              onClick={onExport}
              data-testid="button-export"
              className="hover-elevate active-elevate-2"
            >
              <Download className="h-4 w-4 mr-2" />
              Exportar
            </Button>
            {!isAdmin && (
              <Button
                variant="outline"
                size="default"
                onClick={() => setShowAdminLogin(true)}
                data-testid="button-admin-access"
                className="hover-elevate active-elevate-2"
              >
                <Lock className="h-4 w-4 mr-2" />
                Acesso Admin
              </Button>
            )}
            {isAdmin && (
              <>
                <Button
                  onClick={onNewParticipant}
                  data-testid="button-new-participant"
                  className="bg-orange hover:bg-orange/90"
                >
                  <UserPlus className="h-4 w-4 mr-2" />
                  Novo Cadastro
                </Button>
                <Button
                  variant="outline"
                  size="default"
                  onClick={handleLogout}
                  data-testid="button-logout"
                  className="hover-elevate active-elevate-2"
                >
                  <LogOut className="h-4 w-4 mr-2" />
                  Sair
                </Button>
              </>
            )}
            <ThemeToggle />
          </div>
        </div>
      </header>

      <Dialog open={showAdminLogin} onOpenChange={setShowAdminLogin}>
        <DialogContent data-testid="modal-admin-login">
          <DialogHeader>
            <DialogTitle>Acesso Administrativo</DialogTitle>
            <DialogDescription>
              Digite a senha para acessar o modo de administração
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleAdminLogin} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="admin-password">Senha de Administrador</Label>
              <Input
                id="admin-password"
                type="password"
                placeholder="Digite a senha"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                data-testid="input-admin-password"
                required
                autoFocus
              />
            </div>
            <div className="flex justify-end gap-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => {
                  setShowAdminLogin(false);
                  setPassword('');
                }}
                data-testid="button-cancel-admin"
              >
                Cancelar
              </Button>
              <Button type="submit" data-testid="button-submit-admin">
                Entrar
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/ParticipantCard.ts.content:
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Mail, MapPin, Phone, MessageCircle, Copy } from 'lucide-react';
import type { Participant } from '@/lib/types';
import { DEPARTAMENTOS } from '@/lib/types';
import { useToast } from '@/hooks/use-toast';

interface ParticipantCardProps {
  participant: Participant;
  onClick?: () => void;
}

export default function ParticipantCard({ participant, onClick }: ParticipantCardProps) {
  const { toast } = useToast();

  const initials = participant.nome
    .split(' ')
    .map(n => n[0])
    .join('')
    .toUpperCase()
    .slice(0, 2);

  const handleWhatsApp = (e: React.MouseEvent, phone: string) => {
    e.stopPropagation();
    const cleanPhone = phone.replace(/\D/g, '');
    window.open(`https://wa.me/${cleanPhone}`, '_blank');
  };

  const handleCopyEmail = (e: React.MouseEvent, email: string) => {
    e.stopPropagation();
    navigator.clipboard.writeText(email);
    toast({
      title: "Email copiado!",
      description: "O email foi copiado para a área de transferência.",
    });
  };

  return (
    <Card 
      className="p-4 hover-elevate active-elevate-2 cursor-pointer transition-all h-full flex flex-col"
      onClick={onClick}
      data-testid={`card-participant-${participant.id}`}
    >
      <div className="flex items-start gap-3 mb-3">
        <div className="flex-shrink-0 w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center">
          <span className="text-base font-semibold text-primary">{initials}</span>
        </div>
        <div className="flex-1 min-w-0">
          <h3 className="font-semibold text-base text-foreground" data-testid={`text-name-${participant.id}`}>
            {participant.nome}
          </h3>
          <p className="text-xs text-muted-foreground mt-0.5">
            {participant.assignments.length} {participant.assignments.length === 1 ? 'departamento' : 'departamentos'}
          </p>
        </div>
      </div>

      <div className="flex flex-wrap gap-1.5 mb-3">
        {participant.assignments.map((assignment, idx) => {
          const dept = DEPARTAMENTOS[assignment.departamento];
          return (
            <Badge 
              key={idx}
              className={`text-xs ${dept.bgColor} border-0`}
              data-testid={`badge-assignment-${participant.id}-${idx}`}
            >
              {dept.nome}
            </Badge>
          );
        })}
      </div>

      <div className="space-y-1.5 text-sm mt-auto">
        <div className="flex items-center gap-2 text-muted-foreground">
          <Phone className="h-3.5 w-3.5 flex-shrink-0" />
          <span className="truncate" data-testid={`text-phone-${participant.id}`}>{participant.telefone}</span>
          <Button
            size="icon"
            variant="ghost"
            className="h-6 w-6 ml-auto"
            onClick={(e) => handleWhatsApp(e, participant.telefone)}
            data-testid={`button-whatsapp-${participant.id}`}
          >
            <MessageCircle className="h-3.5 w-3.5" />
          </Button>
        </div>
        <div className="flex items-center gap-2 text-muted-foreground">
          <Mail className="h-3.5 w-3.5 flex-shrink-0" />
          <span className="truncate" data-testid={`text-email-${participant.id}`}>{participant.email}</span>
          <Button
            size="icon"
            variant="ghost"
            className="h-6 w-6 ml-auto"
            onClick={(e) => handleCopyEmail(e, participant.email)}
            data-testid={`button-copy-email-${participant.id}`}
          >
            <Copy className="h-3.5 w-3.5" />
          </Button>
        </div>
        <div className="flex items-center gap-2 text-muted-foreground">
          <MapPin className="h-3.5 w-3.5 flex-shrink-0" />
          <span className="truncate" data-testid={`text-location-${participant.id}`}>{participant.cidadeEstado}</span>
        </div>
      </div>
    </Card>
  );
}

client/src/components/ParticipantDetails.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card } from '@/components/ui/card';
import { Mail, MapPin, Phone, Edit, Trash2, MessageCircle, Copy } from 'lucide-react';
import type { Participant } from '@/lib/types';
import { DEPARTAMENTOS, type DepartamentoKey } from '@/lib/types';
import { useAuth } from '@/contexts/AuthContext';
import { useToast } from '@/hooks/use-toast';

interface ParticipantDetailsModalProps {
  participant: Participant | null;
  open: boolean;
  onClose: () => void;
  onEdit?: (participant: Participant) => void;
  onDelete?: (participant: Participant) => void;
}

export default function ParticipantDetailsModal({ 
  participant, 
  open, 
  onClose,
  onEdit,
  onDelete 
}: ParticipantDetailsModalProps) {
  const { isAdmin } = useAuth();
  const { toast } = useToast();

  if (!participant) return null;

  const handleWhatsApp = (phone: string) => {
    const cleanPhone = phone.replace(/\D/g, '');
    window.open(`https://wa.me/${cleanPhone}`, '_blank');
  };

  const handleCopyEmail = (email: string) => {
    navigator.clipboard.writeText(email);
    toast({
      title: "Email copiado!",
      description: "O email foi copiado para a área de transferência.",
    });
  };

  const initials = participant.nome
    .split(' ')
    .map(n => n[0])
    .join('')
    .toUpperCase()
    .slice(0, 2);

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-lg" data-testid="modal-participant-details">
        <DialogHeader>
          <DialogTitle className="font-display text-2xl">Detalhes do Participante</DialogTitle>
        </DialogHeader>

        <div className="space-y-6 mt-4">
          <div className="flex items-center gap-4">
            <div className="w-16 h-16 rounded-full bg-primary/10 flex items-center justify-center">
              <span className="text-xl font-semibold text-primary">{initials}</span>
            </div>
            <div className="flex-1">
              <h3 className="font-semibold text-xl" data-testid="text-participant-name">
                {participant.nome}
              </h3>
              <p className="text-sm text-muted-foreground mt-1">
                {participant.assignments.length} {participant.assignments.length === 1 ? 'departamento' : 'departamentos'}
              </p>
            </div>
          </div>

          <div className="space-y-4">
            <div className="space-y-3">
              <Label>Departamentos e Funções</Label>
              <div className="space-y-2">
                {participant.assignments.map((assignment, idx) => {
                  const dept = DEPARTAMENTOS[assignment.departamento];
                  return (
                    <Card key={idx} className="p-3">
                      <div className="flex items-start gap-2">
                        <Badge className={`${dept.bgColor} border-0 flex-shrink-0`}>
                          {dept.nome}
                        </Badge>
                        <p className="text-sm flex-1" data-testid={`text-assignment-${idx}`}>
                          {assignment.funcao}
                        </p>
                      </div>
                    </Card>
                  );
                })}
              </div>
            </div>

            <div className="space-y-2">
              <Label>Contato</Label>
              <div className="space-y-2">
                <div className="flex items-center gap-2 text-sm">
                  <Phone className="h-4 w-4 text-muted-foreground" />
                  <span data-testid="text-participant-phone">{participant.telefone}</span>
                  <Button
                    size="icon"
                    variant="ghost"
                    className="h-7 w-7 ml-auto"
                    onClick={() => handleWhatsApp(participant.telefone)}
                    data-testid="button-whatsapp-modal"
                  >
                    <MessageCircle className="h-4 w-4" />
                  </Button>
                </div>
                <div className="flex items-center gap-2 text-sm">
                  <Mail className="h-4 w-4 text-muted-foreground" />
                  <span data-testid="text-participant-email">{participant.email}</span>
                  <Button
                    size="icon"
                    variant="ghost"
                    className="h-7 w-7 ml-auto"
                    onClick={() => handleCopyEmail(participant.email)}
                    data-testid="button-copy-email-modal"
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </div>

            <div className="space-y-2">
              <Label>Localização</Label>
              <div className="flex items-center gap-2 text-sm">
                <MapPin className="h-4 w-4 text-muted-foreground" />
                <span data-testid="text-participant-location">{participant.cidadeEstado}</span>
              </div>
            </div>
          </div>

          {isAdmin && (
            <div className="flex justify-end gap-3 pt-4 border-t">
              <Button
                variant="outline"
                onClick={() => {
                  onEdit?.(participant);
                  onClose();
                }}
                data-testid="button-edit-participant"
              >
                <Edit className="h-4 w-4 mr-2" />
                Editar
              </Button>
              <Button
                variant="destructive"
                onClick={() => {
                  onDelete?.(participant);
                  onClose();
                }}
                data-testid="button-delete-participant"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Excluir
              </Button>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

function Label({ children }: { children: React.ReactNode }) {
  return <p className="text-sm font-medium text-muted-foreground">{children}</p>;
}

